/**
 * Utility types for easy management of asset classes.
 */
import { Asset, AssetDynamic, AssetStatic } from "./asset";
export declare type VaultV1 = Asset<"VAULT_V1">;
export declare type VaultV2 = Asset<"VAULT_V2">;
export declare type VaultV1Static = AssetStatic<"VAULT_V1">;
export declare type VaultV2Static = AssetStatic<"VAULT_V2">;
export declare type VaultV1Dynamic = AssetDynamic<"VAULT_V1">;
export declare type VaultV2Dynamic = AssetDynamic<"VAULT_V2">;
export declare type Vault = VaultV1 | VaultV2;
export declare type VaultStatic = VaultV1Static | VaultV2Static;
export declare type VaultDynamic = VaultV1Dynamic | VaultV2Dynamic;
export declare type IronBankMarket = Asset<"IRON_BANK_MARKET">;
export declare type IronBankMarketStatic = AssetStatic<"IRON_BANK_MARKET">;
export declare type IronBankMarketDynamic = AssetDynamic<"IRON_BANK_MARKET">;
